#include <iostream>
using namespace std;

class Solution
{
public:
    bool isSubsequence(string s, string t)
    {
        // p is for string t
        // q is for string s
        int p = 0, q = 0;
        while (true)
        {
            if (q >= s.length())
            {
                return true;
            }
            if (p >= t.length())
            {
                return false;
            }
            if (s[q] == t[p])
            {
                p++;
                q++;
            }
            else
            {
                p++;
            }
        }
        return false;
    }
};

int main()
{
    // string s = "abc", t = "ahbgdc";
    // string s = "axc", t = "ahbgdc";
    string s = "bcd", t = "";
    Solution q;
    cout << q.isSubsequence(s, t);
    return 0;
}